import React, { Component } from "react";
import { ProductConsumer } from "../context";
import { Link } from "react-router-dom";
import { ButtonContainer } from "./Button";

export default class Details extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const {
            id,
            developer,
            image,
            info,
            price,
            title,
            inCart
          } = value.detailProduct;
          return (
            <div className="container py-5">
              {/* Title */}
              <div className="row">
                <div className="col-10 mx-auto text-center text-slanted text-blue my-5">
                  <h1>{title}</h1>
                </div>
              </div>
              {/* Product Image */}
              <div className="row">
                <div className="col-10 mx-auto col-md-6 my-3">
                  <img src={image} className="img-fluid" alt="product"></img>
                </div>
                {/* Product Description */}
                <div className="col-10 mx-auto col-md-4 my-3 text-capitalize">
                  <h2> Title: {title}</h2>
                  <h4 className="text-title text-uppercase text-muted mt-3 mb-2">
                    Developer:{" "}
                    <span className="text-uppercase">{developer}</span>
                  </h4>
                  <h4 className="text-blue">
                    <strong>
                      price: <span>Â£</span> {price}
                    </strong>
                  </h4>
                  <p className="text-capitalize font-weight-bold mt-3 mb-0">Description: </p>
                  <p></p>
                </div>
              </div>
            </div>
          );
        }}
      </ProductConsumer>
    );
  }
}
