import React, { Component } from "react";
import CartLayout from "../components/ShoppingCart/CartLayout";
import Title from "../components/Title";
import EmptyCart from "../components/ShoppingCart/EmptyCart";
import { ProductConsumer } from "../context";
import CartList from "../components/ShoppingCart/CartList";
import CartTotal from "../components/ShoppingCart/CartTotal"

export default class ShoppingCart extends Component {
  render() {
    return (
      <section>
        <ProductConsumer>
          {value => {
            const { cart } = value;
            if (cart.length > 0) {
              return (
                <React.Fragment>
                  <Title name="shopping" title="cart"></Title>
                  <CartLayout />
                  <CartList value={value} />
                  <CartTotal value={value} />
                </React.Fragment>
              );
            } else {
              return <EmptyCart />;
            }
          }}
        </ProductConsumer>
      </section>
    );
  }
}
