import React, { Component } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { ProductConsumer } from "../context";

export default class Product extends Component {
  render() {
    const { id, title, image, price, inCart } = this.props.product;
    return (
      <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
        <div className="card">
          <div
            className="img-container p-5"
            onClick={() => console.log("Click on image container")}
          >
            <Link to="/details">
              <img src={image} alt="product" className="card-img-top"></img>
            </Link>
            <button
              className="cart-btn"
              disabled={inCart ? true : false}
              onClick={()=>{console.log("Added to the cart")}}
            ></button>
              {inCart?(<p className="text-capitalize mb-0" disabled> in cart</p>):(<i className="fas-fa cart"></i>)}
          </div>
        </div>
        <h3>Hello from product</h3>
      </ProductWrapper>
    );
  }
}

const ProductWrapper = styled.div``;
