import React, { Component } from "react";
import { storeProducts, detailProduct } from "./data";

// Creating a provider and consumer for the information
const ProductContext = React.createContext();

class ProductProvider extends Component {
  state = {
    products: [],
    detailProduct: detailProduct,
    cart: [],
    modalOpen: false,
    modalProduct: detailProduct,
    cartVAT: 0,
    cartTotal: 0
  };

  componentDidMount() {
    this.setProducts();
  }

  // Obtain copy of values from data.js
  setProducts = () => {
    let tempProducts = [];
    storeProducts.forEach(item => {
      const singleItem = { ...item };
      tempProducts = [...tempProducts, singleItem];
    });
    this.setState(() => {
      return { products: tempProducts };
    });
  };

  // Get the id values of an item
  getItem = id => {
    const product = this.state.products.find(item => item.id === id);
    return product;
  };

  // Set the template product to our current product
  handleDetail = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { detailProduct: product };
    });
  };

  // Add the specific object to cart using id
  addToCart = id => {
    let tempProducts = [...this.state.products];
    const index = tempProducts.indexOf(this.getItem(id));
    const product = tempProducts[index];
    product.inCart = true;
    product.count = 1;
    const price = product.price;
    product.total = price;
    this.setState(
      () => {
        return { products: tempProducts, cart: [...this.state.cart, product] };
      },
      () => {
        this.addTotals();
      }
    );
  };

  // Open modal when clicked on image
  openModal = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { modalProduct: product, modalOpen: true };
    });
  };

  // Close modal when clicked back or checkout
  closeModal = () => {
    this.setState(() => {
      return { modalOpen: false };
    });
  };

  increment = id => {
    // Get cart values from the current state
    let tempCart = [...this.state.cart];
    const incProduct = tempCart.find(item => item.id === id);

    // Get index of item to be incremented
    const index = tempCart.indexOf(incProduct);
    const product = tempCart[index];

    product.count += 1;
    product.total = product.count * product.price;

    this.setState(() => {
      return {
        cart: [...tempCart]
      };
    }, this.addTotals());
  };

  decrement = id => {
    let tempCart
  };

  // Remove a single item from cart
  removeItem = id => {
    // Get products from the current state
    let tempProducts = [...this.state.products];

    // Get cart values from the current state
    let tempCart = [...this.state.cart];

    tempCart = tempCart.filter(item => item.id !== id);

    const index = tempProducts.indexOf(this.getItem(id));
    let removeProduct = tempProducts[index];

    removeProduct.inCart = false;
    removeProduct.count = 0;
    removeProduct.total = 0;

    this.setState(
      () => {
        return {
          cart: [...tempCart],
          product: [...tempProducts]
        };
      },
      () => {
        this.addTotals();
      }
    );
  };

  // Clear the cart and get new copies of object data
  clearCart = id => {
    this.setState(
      () => {
        return { cart: [] };
      },
      () => {
        this.setProducts();
        this.addTotals();
      }
    );
  };

  // Add totals from current cart and then include VAT
  addTotals = () => {
    let subTotal = 0;
    this.state.cart.map(item => (subTotal += item.total));
    const tempVAT = subTotal * 0.2;
    const VAT = parseFloat(tempVAT).toFixed(2);

    const tempTotal = subTotal + VAT;
    const total = parseFloat(tempTotal).toFixed(2);

    this.setState(() => {
      return {
        cartVAT: VAT,
        cartTotal: total
      };
    });
  };

  render() {
    return (
      <ProductContext.Provider
        value={{
          ...this.state,
          handleDetail: this.handleDetail,
          addToCart: this.addToCart,
          openModal: this.openModal,
          closeModal: this.closeModal,
          increment: this.increment,
          decrement: this.decrement,
          removeItem: this.removeItem,
          clearCart: this.clearCart
        }}
      >
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer };
