import React, { Component } from "react";
import { storeProducts, detailProduct } from "./data";

// Creating a provider and consumer for the information
const ProductContext = React.createContext();

class ProductProvider extends Component {
  state = {
    products: [],
    detailProduct: detailProduct,
    cart: item,
    modalOpen: false,
    modalProduct: detailProduct,
    cartSubTotal: 0,
    cartVAT: 0,
    cartTotal: 0
  };

  componentDidMount() {
    this.setProducts();
  }

  // Obtain copy of values from data.js
  setProducts = () => {
    let tempProducts = [];
    storeProducts.forEach(item => {
      const singleItem = { ...item };
      tempProducts = [...tempProducts, singleItem];
    });
    this.setState(() => {
      return { products: tempProducts };
    });
  };

  // Get the id values of item
  getItem = id => {
    const product = this.state.products.find(item => item.id === id);
    return product;
  };

  // Set the template product to our current product
  handleDetail = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { detailProduct: product };
    });
  };

  // Add the specific object to cart using id
  addToCart = id => {
    let tempProducts = [...this.state.products];
    const index = tempProducts.indexOf(this.getItem(id));
    const product = tempProducts[index];
    product.inCart = true;
    product.count = 1;
    const price = product.price;
    product.total = price;
    this.setState(
      () => {
        return { products: tempProducts, cart: [...this.state.cart, product] };
      },
      () => {
        console.log(this.state);
      }
    );
  };

  // Open modal when clicked on image
  openModal = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { modalProduct: product, modalOpen: true };
    });
  };

  // Close modal when clicked back or checkout
  closeModal = () => {
    this.setState(() => {
      return { modalOpen: false };
    });
  };

  increment = id => {
    console.log("This is increment method");
  };

  decrement = id => {
    console.log("This is decrement method");
  };

  removeItem = id => {
    console.log("Item has been removed");
  };

  clearCart = id => {
    console.log("Clear cart method");
  };

  render() {
    return (
      <ProductContext.Provider
        value={{
          ...this.state,
          handleDetail: this.handleDetail,
          addToCart: this.addToCart,
          openModal: this.openModal,
          closeModal: this.closeModal,
          increment: this.increment,
          decrement: this.decrement,
          removeItem: this.removeItem,
          clearCart: this.clearCart
        }}
      >
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer };
